import './App.css';

function App() {

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");


  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "password") {
      setMessage("Login successful!");
    } else {
      setMessage("Login failed. Please try again.");
    }
  };

  return (
    <div className="App">
       <div className="flex-container">
      <div className="signup-nav">
        {/* <h1>Pulse</h1> */}
      </div>
      <div className="details-login">
        <form className="flex-login" onSubmit={handleSubmit}>
          <div className="pulse">
            <h1 data-text="WELCOME TO PULSE "> WELCOME TO PULSE </h1>
          </div>
          <div className="user-container">
          <div className="user">
            <input
              type="text"
              id="username"
              placeholder="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="user">
            <input
              type="password"
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          </div>
          <div className="login-with">
            <p>Or login with</p>
          </div>
          <div className="signup-btn">
            <button
              className="email-integration-2"
              onClick={handleGoogleSignIn}
            >
              Sign in with Google
            </button>
            <button
              className="email-integration-3"
              onClick={handleOutlookSignIn}
            >
              Sign in with Outlook
            </button>
            <button className="signup-btn1">Login</button>
            <div className="login-signin-container">
             <p>Don't have account?</p>  
            <div
              className="signup-btn2"
              onClick={() => handleMenuClick("/")}
            >
                Signup Now
            </div>
            </div>
          </div>
        </form>
        {message && <p>{message}</p>}
      </div>
      </div>
    </div>
  );
}

export default App;
