import { useEffect, useState } from 'react';
import './App.css';



function App() {

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [data, setData] = useState(null);

  useEffect(() => {
    // Call the FastAPI backend
    fetch('http://localhost:8000/api/data')
      .then((response) => response.json())
      .then((data) => setData(data.message))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);


  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "password") {
      setMessage("Login successful!");
    } else {
      setMessage("Login failed. Please try again.");
    }
  };

  const handleMenuClick = (path) => {
    // navigate(path);
    console.log ('')
  };

  const handleGoogleSignIn = () => {
    console.log("Signing in with Google...");
    // Add Google sign-in logic here
  };

  const handleOutlookSignIn = () => {
    console.log("Signing in with Outlook...");
    // Add Outlook sign-in logic here
  };

  return (

       <div className="flex-container">
      <div className="signup-nav">
      </div>
      <div className="details-login">
        <form className="flex-login" onSubmit={handleSubmit}>
          <div className="pulse">
            <h1 data-text="WELCOME TO TESTS "> WELCOME TO TESTS</h1>
          </div>
          <div className="user-container">
          <div className="user">
            <input
              type="text"
              id="username"
              placeholder="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="user">
            <input
              type="password"
              id="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          </div>
          <div className="login-with">
            <p>Or login with</p>
          </div>
          <div className="signup-btn">
            <button
              className="email-integration-2"
              onClick={handleGoogleSignIn}
            >
              Sign in with Google
            </button>
            <button
              className="email-integration-3"
              onClick={handleOutlookSignIn}
            >
              Sign in with Outlook
            </button>
            <button className="signup-btn1">Login</button>
            <div className="login-signin-container">
             <p>Don't have account?</p>  
            <div
              className="signup-btn2"
              onClick={() => handleMenuClick("/")}
            >
                Signup Now
            </div>
            </div>
          </div>
        </form>
        {message && <p>{message}</p>}
      </div>
      </div>

  );
}

export default App;
